"use strict";const addContact=async(e,t)=>{const r=await fetch(BASEAPI+e,{method:"POST",body:t}),n=await r.json();if(""===n.error){document.querySelector(".alert-success").classList.remove("d-none"),clearInputsForm()}else{let e=document.querySelector(".alert-danger");e.classList.remove("d-none"),e.innerHTML=n.error}};let submitBtn=document.querySelector("#submit-btn");function clearInputsForm(){let e=document.querySelector(".form-budget");[...e.querySelectorAll("input[type=text]")].forEach(e=>{e.value=""}),e.querySelector("textarea").innerHTML=""}submitBtn.addEventListener("click",e=>{e.preventDefault();let t=document.querySelector(".form-budget"),r=new FormData(t);addContact("add_client",r)});