"use strict";let cardsPortfolio=[...document.querySelectorAll(".portfolio__card")];function initPortfolio(){cardsPortfolio.forEach(t=>{let e=t.querySelector(".portfolioBtn-prev"),o=t.querySelector(".portfolioBtn-next");e.addEventListener("click",()=>{prevBtnAction(t)}),o.addEventListener("click",()=>{nextBtnAction(t)})})}function prevBtnAction(t){let e=t.querySelector(".portfolio__img").clientWidth,o=t.querySelector(".portfolio__img--subcontainer"),r=parseFloat(getComputedStyle(o).marginLeft);r<0&&(o.style.marginLeft=r+e+"px")}function nextBtnAction(t){let e=t.querySelector(".portfolio__img").clientWidth,o=t.querySelector(".portfolio__img--subcontainer"),r=-e*t.querySelectorAll(".portfolio__img").length,i=parseFloat(getComputedStyle(o).marginLeft);i>r+e&&(o.style.marginLeft=i-e+"px")}initPortfolio();